.prechatContent .fieldsList {
    margin: 0.75rem 0.75rem 0;
}

.prechatContent .uiInput {
    margin: 0 0.75rem 0.375rem;
    text-align: left;
}

.prechatContent .fieldsList .uiInput:nth-child(1)  {
    display: inline-block;
    margin: 0 0.375rem 0.375rem 0.75rem;
    width: calc(50% - 1.125rem);
}

.prechatContent .fieldsList .uiInput:nth-child(2)  {
    display: inline-block;
    margin: 0 0.75rem 0.375rem 0.375rem;
    width: calc(50% - 1.125rem);
}

.prechatContent .uiLabel {
    color: rgb(0, 0, 0);
    font-size: .75em;
    margin-left: .5em;
    margin-right: .75em;
    position: relative;
    text-align: left;
}

.prechatContent .input, .prechatContent .select {
    border: 1px solid rgb(0, 0, 0);
    font-size: .875em;
    height: 44px !important;
    line-height: 30px !important;
    padding: 0 12px;
    transition: border .1s linear,background-color .1s linear;
}

.prechatContent .input:active, .prechatContent .select:active, .prechatContent .input:focus, .prechatContent .select:focus {
    border: 2px solid rgb(0, 0, 0);
    box-shadow: 0 0 2px 0 rgb(27, 185, 213);
    outline: none;
}

.prechatContent .select {
    background-image: url(https://c.cs40.visual.force.com/resource/1577121571000/Brightcove_Select_Arrow?) !important;
    background-position: 96% 51% !important;
    background-size: 12px 7px !important;
}

.prechatContent .required {
    color: rgb(255, 80, 0);
    float: none;
    left: -6px;
    padding: unset;
    position: absolute;
    top: 0;
}

.prechatUI .startButtonWrapper {
    background: linear-gradient(to bottom, rgba(255, 255, 255, 0) 0%, rgba(255, 255, 255, 1) calc(100% - 77px), rgba(255, 255, 255, 1) 100%);
    bottom: 0;
    box-sizing: border-box;
    margin: 0;
    padding: 30px 24px 24px;
    position: absolute;
    text-align: center;
    width: 100%;
}

.prechatUI .startButtonWrapper .startButton {
    background: rgb(255, 80, 0);
    border: none;
    border-radius: 4px;
    box-shadow: none;
    color: rgb(255, 206, 0);
    font-family: Arial;
    font-size: 1em;
    font-weight: 900;
    height: 44px;
    margin: 0;
    position: relative;
    text-shadow: none;
    transition: background .4s ease;
    width: 100%;
}

