/*LOGIN GRID STYLES*/

div.container {
    margin: 20px auto;
}

div.center{
    margin: 10px;
}

h5.grid{
    font-size: 13px !important;
}
ul.column {
    float: left;
    padding: 0;
    list-style: none;
    width: 100%;
}
ul.column li{
    background: #fff;
    float: left;
    margin: 25px 0 0 25px;
    display: block;
    text-align: center;
    width: 250px;
    height: 250px;
    padding:20px;
    border-radius: 2px 2px 2px 2px;
    -webkit-touch-callout: none;
    -webkit-user-select: none;
    -khtml-user-select: none;
    -moz-user-select: none;
    -ms-user-select: none;
    user-select: none;
    list-style: none !important;

}

ul.column li.box{
    border-bottom: 1px solid rgba(0, 0, 0, 0.2);
    box-shadow: 0 1px 5px rgba(0, 0, 0, 0.15);
    list-style: none !important;
}

li.box ::before {
    content: none;
}

img.grid{
    width: 70px;
    height: 60px;
    margin: 15px;
}

/* Clear Float */
.column:after, .box-collection:after, .clearfix:after, div.project-info:after {
    clear: both;
    content: ' ';
    display: block;
    font-size: 0;
    line-height: 0;
    visibility: hidden;
    width: 0;
    height: 0;
    list-style: none !important;
}

div.login{
    text-align: center;
    padding-top: 80px;
}

