ValidationRule.errorConditionFormula:
AND(
RecordTypeId = '0128F000000W684QAC', /*Technical Support*/ ISBLANK( Case_Close_Summary__c), OR( ISPICKVAL(Status,'Closed'), ISPICKVAL(Status,'Closed as Duplicate'), ISPICKVAL(Status,'Closed First Response'), ISPICKVAL(Status,'Closed - Unresolved') ), OR( $UserRole.Name = "Technical Support Manager", $UserRole.Name = "Technical Support Rep" )
)
