CustomField.formula:
IF( ISNULL( NPS_Rating_Latest__c ) , "None", 

   IF( AND( NOT(ISNULL(NPS_Rating_Latest__c)) , ISNULL(NPS_Rating_Earliest__c) ), "New", 

        IF( NPS_Rating_Latest__c > NPS_Rating_Earliest__c , "Increasing" , 

            IF( NPS_Rating_Latest__c < NPS_Rating_Earliest__c , "Decreasing", 

                IF( AND( NPS_Rating_Latest__c = NPS_Rating_Earliest__c, NPS_Rating_Date_Latest__c = NPS_Rating_Date_Earliest__c ), "New", 

                    IF( AND( NPS_Rating_Latest__c = NPS_Rating_Earliest__c, NOT(NPS_Rating_Date_Latest__c = NPS_Rating_Date_Earliest__c) ), "Same", 

                        "Unknown" 

)
)
)
)
)
)
